INSERT INTO `recent_news` (`id`, `news_title`, `news_post`, `new_date`) VALUES
(1, '"আউশ উৎপাদন বৃদ্ধির কর্ম পরিকল্পনা ও বোরো উৎপাদন ব্যবস্থাপনা’’ শীর্ষক কর্মশালা', '২৭ মার্চ/২০১৪ খ্রি : বিএআরসি মিলনায়তনে আউশ উৎপাদন বৃদ্ধির কর্মপরিকল্পনা ও বোরো উৎপাদন ব্যবস্থাপনা শীর্ষক কর্মশালা অনুষ্ঠিত হয়। কর্মশালায় প্রধান অতিথি হিসেবে উপস্থিত ছিলেন কৃষিমন্ত্রী মতিয়া চৌধুরী, এম,পি। <br>\r\n\r\nউক্ত কর্মশালায় সভাপতিত্ব করেন কৃষি সচিব ড. এস এম নাজমুল ইসলাম, বিশেষ অতিথি ছিলেন জনাব মো. জহির উদ্দিন আহমেদ, এনডিসি, চেয়ারম্যান, বিএডিসি। সম্মানিত অতিথি হিসেবে উপস্থিত ছিলেন ড. মো. কামাল উদ্দিন, নির্বাহী চেয়ারম্যান, বিএআরসি। অনুষ্ঠানে স্বাগত বক্তব্য ও আউশ উৎপাদন বৃদ্ধির  কলাকৌশল ও পরিকল্পনা উপস্থাপন করেন কৃষি সম্প্রসারণ অধিদপ্তরের মহাপরিচালক কৃষিবিদ মো. আবু হানিফ মিয়া। <br>\r\n\r\nকৃষিমন্ত্রী তাঁর বক্তব্যে বলেন, প্রাচীনকাল থেকেই প্রাকৃতিক উপায়ে আউশের চাষ করা হচ্ছে বলে এটি সহজাত উৎপাদিত দানাদার শস্য। কৃষি বিপ্লবের ফলে বোরো আবাদ বৃদ্ধি পেলেও আধুনিক যান্ত্রিকীকরণের মাধ্যমে আউশের আবাদী জমি ও উৎপাদন উভয়ই বাড়ানো সম্ভব। আউশ আবাদের ব্যাপক উপকারিতা বর্ণনা করে সরকার কর্তৃক গৃহীত আউশ প্রণোদনা কর্মসূচির সফল বাস্তবায়নের লক্ষ্যে কৃষিমন্ত্রী সকলকে নিরলসভাবে কাজ করার আহবান জানান। আগামীতে আউশ উৎপাদন বৃদ্ধিতে সর্বস্তরের সকলকে এগিয়ে আসার জন্য আহ্বান জানান। <br>\r\n\r\nকর্মশালায় কৃষি সংশ্লিষ্ট বিভিন্ন সরকারি ও বেসরকারি প্রতিষ্ঠানের কর্মকর্তা, গবেষণা প্রতিষ্ঠানের কৃষি বিজ্ঞানী এবং কৃষকগণ উপস্থিত ছিলেন। প্রেস বিজ্ঞপ্তি', '2014-05-28 03:09:16'),
(2, '''স্বাধীনতা পুরষ্কার ২০১৪’-এ ভূষিত হলো বাংলাদেশ কৃষি গবেষণা ইনস্টিটিউট', 'বাংলাদেশ সরকারের সর্বোচ্চ বেসামরিক সম্মাননা ‘স্বাধীনতা পুরষ্কার ২০১৪’-এ ভূষিত হয়েছে ৯ জন বিশিষ্ট ব্যক্তি ও একটি প্রতিষ্ঠান। প্রধানমন্ত্রী শেখ হাসিনা মঙ্গলবার ২৫ মার্চ ২০১৪ ওসমানী স্মৃতি মিলনায়তনে বিভিন্ন ক্ষেত্রে অসামান্য অবদানের জন্য নয়জন বিশিষ্ট ব্যক্তি ও বাংলাদেশ কৃষি গবেষণা ইনস্টিটিউটকে চলতি বছরের স্বাধীনতা পুরষ্কার প্রদান করেন। প্রতিষ্ঠান হিসেবে বাংলাদেশ কৃষি গবেষণা ইনস্টিটিউটকে কৃষি ক্ষেত্রে অসামান্য উদ্ভাবন ও গবেষণায় বিশেষ অবদানের জন্য এই  স্বাধীনতা পুরস্কার দেওয়া হয়। ২৬ শে মার্চ মহান স্বাধীনতা দিবসকে সামনে রেখে বিভিন্ন ক্ষেত্রে অবদানের জন্য এই পদক দেয়া হয়ে থাকে।', '2014-05-28 07:19:08'),
(3, 'বিএডিসি কর্তৃক উৎপাদিত সরিষা, র্সূযমুখী, মসুর, ছোলা, খেসারী, মটর ও ফেলন বীজের মূল্য নির্ধারণ', 'বাংলাদেশ কৃষি উন্নয়ন কর্পোরেশন(বিএডিসি) কর্তৃক ১৬র্মাচ ২০১৪ তারিখে অনুষ্ঠিত সভার সিদ্ধান্তক্রমে ২০১৩-১৪ উৎপাদন বছরে রবি মৌসুমে উৎপাদিত সরিষা, র্সূযমুখী, মসুর, ছোলা, খেসারী, মটর ও ফেলন বীজের মূল্য নতুনভাবে নির্ধারণ করা হয়।', '2014-05-28 07:11:02');

